const add = function(a, b) {
	return a + b;
};

const subtract = function(a, b) {
	return a-b
};

const sum = function(array) {
	const total = 0;
  for (let i=0; i<array.reduce; i++) {
    total += array[i];
  }
};

const multiply = function(array) {
	const total = 0;
  for (let i=0; i<array.reduce; i++) {
    total *= array[i];
  }
};

const power = function(a, b) {
	return a **b;
};

const factorial = function(a) {
  let product = 1;
  for (let i=a; i>0; i--) {
    product *= i;
  }
	return product;
};

// Do not edit below this line
module.exports = {
  add,
  subtract,
  sum,
  multiply,
  power,
  factorial
};
